function initWebsocket({ websocketId, projectUrl, anonKey }, ctx) {

    const params = new URLSearchParams({
        apikey: anonKey,
        vsn: '1.0.0'
    });

    const wsUrl = projectUrl.replace('https://', 'wss://').replace('http://', 'ws://') + '/realtime/v1/websocket';

    const ws = new WebSocket(`${wsUrl}?${params}`)

    ws.onopen = () => {
        ctx.triggerActionEvent("onOpen")
    };

    ws.onmessage = (event) => {
        ctx.triggerActionEvent("onMessage", event)
    };

    ws.onclose = () => {
        ctx.triggerActionEvent("onClose")
    };

    ws.onerror = (error) => {
        ctx.triggerActionEvent("onError", error)
    };

    window[websocketId] && window[websocketId].close()
    window[websocketId] = ws

    return () => {
        ws.close()
        window[websocketId] = null
    }
}

