function subscribeToChanges({ websocketId, topic, ref, joinRef, payload }, ctx) {

    const ws = window[websocketId]


    const handleMessage = (message) => {
        const parsedMessage = JSON.parse(message.data)
        parsedMessage.topic === topic && ctx.triggerActionEvent("onMessage", parsedMessage)
    }

    if (ws && ws.readyState === WebSocket.OPEN) {

        ws.addEventListener('message', handleMessage);
        ws.send(JSON.stringify({ event: "phx_join", topic, ref, join_ref: joinRef, payload }));
    }

    return () => {
        ws.removeEventListener('message', handleMessage);
    }
}

