function sendWebsocketMessage({ websocketId, message, isPermanent }, ctx) {

    const ws = window[websocketId]
    const ref = Date.now() + "-" + Math.random().toString(36)

    const handleMessage = (message) => {
        const parsedMessage = JSON.parse(message.data)

        if (parsedMessage.ref === ref) {
            ctx.triggerActionEvent("onReply", parsedMessage)
            !isPermanent && ws.removeEventListener('message', handleMessage);
        }
    }

    if (ws && ws.readyState === WebSocket.OPEN) {

        ws.addEventListener('message', handleMessage);
        ws.send(JSON.stringify({ ...message, ref }));
    }

    return () => {
        ws.removeEventListener('message', handleMessage);
    }
}

